##check balance
from web3 import Web3, HTTPProvider

address = '0x...'
rpc = 'https://kovan.infura.io/v3/9aa3d95b3bc440fa88ea12eaa4456161'

web3 = Web3(HTTPProvider(rpc))
balance = web3.fromWei(web3.eth.getBalance(address), "ether")
print(balance)

